/** The options to build the URL of the V3 API. */

/**
 * Builds the URL for the V3 API.
 * @param urlOptions The options to build the URL of the V3 API.
 */
export function buildUrl(urlOptions) {
  var sendTo = urlOptions.sendTo,
    channelName = urlOptions.channelName;
  var baseUrl = sendTo === 'production' ? 'https://telemetry.elastic.co' : 'https://telemetry-staging.elastic.co';
  return "".concat(baseUrl, "/v3/send/").concat(channelName);
}