import * as t from 'io-ts';
import { SeverityMapping } from '../severity_mapping';

/**
 * Types the DefaultStringArray as:
 *   - If null or undefined, then a default SeverityMapping array will be set
 */
export var DefaultSeverityMappingArray = new t.Type('DefaultSeverityMappingArray', SeverityMapping.is, function (input, context) {
  return input == null ? t.success([]) : SeverityMapping.validate(input, context);
}, t.identity);