import * as t from 'io-ts';
import { threats } from '../threat';

/**
 * Types the DefaultThreatArray as:
 *   - If null or undefined, then an empty array will be set
 */
export var DefaultThreatArray = new t.Type('DefaultThreatArray', threats.is, function (input, context) {
  return input == null ? t.success([]) : threats.validate(input, context);
}, t.identity);