import * as t from 'io-ts';
import { list } from '../lists';

/**
 * Types the DefaultListArray as:
 *   - If null or undefined, then a default array of type list will be set
 */
export var DefaultListArray = new t.Type('DefaultListArray', t.array(list).is, function (input, context) {
  return input == null ? t.success([]) : t.array(list).validate(input, context);
}, t.identity);