export var transformDataToNdjson = function transformDataToNdjson(data) {
  if (data.length !== 0) {
    var dataString = data.map(function (item) {
      return JSON.stringify(item);
    }).join('\n');
    return "".concat(dataString, "\n");
  } else {
    return '';
  }
};